<script setup lang="ts">
const props = defineProps<{ amount: number | null }>()

const emit = defineEmits<{
  (e: 'paid', amount: number): void
}>()

const pay = () => {
  if (!props.amount) {
    alert('لطفا مبلغ را انتخاب کنید')
    return
  }
  alert(`پرداخت به مبلغ ${props.amount.toLocaleString('fa-IR')} تومان انجام می‌شود.`)

  // شبیه‌سازی موفقیت پرداخت
  emit('paid', props.amount)
}
</script>

<template>
  <h3 class="text-[14px] text-navy-700 mt-10 font-medium mr-10">درگاه پرداخت</h3>

  <div
    class="bg-gray-100 h-32 mt-5 border border-gray-300 flex items-center mr-10 ml-10 justify-center gap-6 rounded-xl p-4"
  >
    <img
      class="bank-shadow border border-blue-500 object-contain cursor-pointer h-16 w-26 rounded-xl bg-white shadow-lg transition-transform duration-300 hover:scale-105 hover:border-blue-600"
      alt="بانک ۱"
      src="data:image/jpeg;base64,"
    />
    <img
      class="border border-gray-300 opacity-50 object-contain cursor-not-allowed h-16 w-26 rounded-xl bg-white shadow-md"
      alt="بانک ۲"
      src="data:image/jpeg;base64,"
    />
  </div>
  <div class="flex justify-center mt-2">
    <button @click="pay" class="bg-blue-500 w-30 text-white py-2 rounded-2xl">پرداخت</button>
  </div>
</template>
